reports.query:
MATCH (cu:Customer)<-[:CONSUMED_BY]-(sv:Service)<-[:DELIVERS]-(sw:Software)<-[:SNAPSHOT_OF]-(sn:Snapshot {type: $neodash_snapshot_type})<-[:IDENTIFIED_IN]-(sc:SoftwareComponent {component: $neodash_sw_component})<-[:VULNERABILITY_OF]-(vl:Vulnerability)
RETURN  DISTINCT sv.serviceName as Service, sv.serviceOwner as `Service Owner`, cu.customerName AS Customer, cu.customerImportance AS `Customer Importance`
ORDER BY `Customer Importance` DESC;

MATCH (cu:Customer)<-[:CONSUMED_BY]-(sv:Service)<-[:DELIVERS]-(sw:Software)<-[:SNAPSHOT_OF]-(sn:Snapshot)<-[:IDENTIFIED_IN]-(sc:SoftwareComponent {component: $neodash_softwarecomponent_component})<-[:VULNERABILITY_OF]-(vl:Vulnerability)
RETURN  DISTINCT sv.serviceName as Service, sv.serviceOwner as `Service Owner`, cu.customerName AS Customer, cu.customerImportance AS `Customer Importance`
ORDER BY `Customer Importance` DESC;

MATCH p=(fi:Finding {title:  $neodash_finding_title})-[:IDENTIFIED_IN]->(sn:Snapshot {ref: $neodash_finding_snapshot})-[:SNAPSHOT_OF]->(so:Software {swName: $neodash_snapshot_software})-[:RUNS_ON]->(:Server {serverName : $neodash_snapshot_sw_server})-[:LOCATED_IN]->(dc:Datacenter)-[:IN_SECTION]->(:Section)-[:IN_RACK]->(:Rack)
MATCH q=(dc)-[:HAS_LOCATION]->(dcl:DCLocation)
RETURN q, p;
